mapper.select:
SELECT d.DutyCode, d.DutyName, d.DutyType, s.ScoreType, GROUP_CONCAT(CONCAT(u.UserName,'(',u.MoneyCard,')','(',d1.DepartmentName,'-',s1.StationName,')')) ScorringName FROM duty d left join score s on d.DutyCode = s.DutyCode and s.ScoreType = #{scoreType} and s.ScorredCode = #{scorredcode} left join user u on u.UserCode = s.ScorringCode left join station s1 on u.StationCode = s1.StationCode left join department d1 on d1.DepartmentCode = s1.DepartmentCode where d.dbtype = #{dbtype} and d.StationCode = #{station}
and u.StationCode=#{stationcode}
and u.username=#{username}
group by d.DutyCode, d.DutyName, d.DutyType, s.ScoreType order by d.DutyCode+0
