mapper.select:
select SerialNo, Title, EmployeeCode, Pubdate, Year, Month, State,Content, SavePath,FileName,IsSend, dbtype
from monthsummary


        and Title like concat('%',#{title},"%")


        and Year =#{year}


        and Month =#{month}


        and dbtype =#{dbtype}

    and EmployeeCode =#{employeecode}
    and State not in("0")

order by Year+0 desc, Month+0 desc,Pubdate desc

mapper.delete:
delete from monthsummary
where SerialNo in

    #{serialnos}

mapper.select:
select * from monthsummary where Year=#{year} and Month=#{month} and dbtype =#{dbtype}
and EmployeeCode in

    #{item}
