mapper.select:
select h.Id, h.UserCode, h.Year, h.Month, h.ScoreStatus, h.AScore, h.BScore, h.CScore, h.DScore, h.EScore, h.FScore, h.TotalScore, h.SumZdScore, h.SumMbScore, h.AvgZdScore, h.AvgMbScore, h.SumTotalScore, h.DfScore, h.dbtype, h.SumZfScore, u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.SavePath,m.FileName,m.Title,m.Content, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName,u.dbbk from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and u.UserName like concat('%',#{history.username},'%')
and u.StationCode=#{history.stationcode}
and h.Year=#{history.year}
and h.Month=#{history.month}
and m.State=#{history.state}
and (m.Title is null or m.Title='') and (m.Content is null or m.Content='')
and h.ScoreStatus=#{history.scorestatus}
and m.dbtype = #{history.dbtype,jdbcType=VARCHAR}
and u.postType = #{history.postType}
and u.dbbk = #{history.dbbk,jdbcType=VARCHAR}
and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType='0' and u.Flag='0' and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode
AND u.UserCode in (
select code from ( SELECT ScorredCode code FROM score WHERE dbtype =#{history.dbtype} union SELECT ScorringCode code FROM score WHERE dbtype =#{history.dbtype} ) a
select code from ( select sf.ScoredCode code from monthsummary m,scoreflow sf where m.SerialNo = sf.MSerialNo and m.dbtype = sf.dbtype and m.year = #{history.year} and m.month=#{history.month} and m.dbtype=#{history.dbtype} select sf.ScorringCode code from monthsummary m,scoreflow sf where m.SerialNo = sf.MSerialNo and m.dbtype = sf.dbtype and m.year = #{history.year} and m.month=#{history.month} and m.dbtype=#{history.dbtype} ) a
)
AND u.UserCode in (
select ScorringCode from (SELECT DISTINCT ScorringCode FROM score WHERE dbtype =#{history.dbtype}) a
select ScorringCode from ( select DISTINCT sf.ScorringCode from monthsummary m,scoreflow sf where m.SerialNo = sf.MSerialNo and m.dbtype = sf.dbtype and m.year = #{history.year} and m.month=#{history.month} and m.dbtype=#{history.dbtype}) a
)
AND u.UserCode in (
select ScorredCode from (SELECT DISTINCT ScorredCode FROM score WHERE dbtype =#{history.dbtype}) a
select ScoredCode from ( select DISTINCT sf.ScoredCode from monthsummary m,scoreflow sf where m.SerialNo = sf.MSerialNo and m.dbtype = sf.dbtype and m.year = #{history.year} and m.month=#{history.month} and m.dbtype=#{history.dbtype}) a
)
and h.Year=m.Year and m.Month=h.Month AND h.dbtype = m.dbtype
order by h.Year+0 desc, h.Month+0 desc

select h.*, u.UserCode,u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.ScoreStatus, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and u.UserName like concat('%',#{history.username},'%')
and u.StationCode=#{history.stationcode}
and h.Year=#{history.year}
and h.Month=#{history.month}
and m.State=#{history.state}
and (m.Title is null or m.Title='') and (m.Content is null or m.Content='')
and h.ScoreStatus=#{history.scorestatus}
and m.dbtype = #{history.dbtype,jdbcType=VARCHAR}
and m.dbbk = #{history.dbbk,jdbcType=VARCHAR}
and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType='0' and u.Flag='0' and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode and u.userCode in(
select code from (select distinct ScorringCode code from score where dbtype= #{history.dbtype}) a
select ScorringCode from ( select DISTINCT sf.ScorringCode from monthsummary m,scoreflow sf where m.SerialNo = sf.MSerialNo and m.dbtype = sf.dbtype and m.year = #{history.year} and m.month=#{history.month} and m.dbtype=#{history.dbtype}) a
) and h.Year=m.Year and m.Month=h.Month AND h.dbtype = m.dbtype
order by h.Year+0 desc, h.Month+0 desc

select h.* from  scorehistory h,user u,user_role ur
where h.UserCode=u.UserCode
and  u.UserCode=ur.UserCode
and u.userCode in(

        select code from (select distinct ScorringCode code from score where dbtype= #{history.dbtype}) a


