        m.dbtype = sf.dbtype and m.year = #{history.year} and m.month=#{history.month} and m.dbtype=#{history.dbtype}) a

)
and  u.Flag='0'
and  u.UserState='1'
and  h.Year=#{history.year}
and  h.Month=#{history.month}

        and h.dbtype = #{history.dbtype}


        and h.dbbk = #{history.dbbk}

