select h.*, u.UserCode,u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.State,m.ScoreStatus, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from scorehistory h, user u ,monthsummary m,station s,department d,role r,user_role ur
and h.Year=#{year}
and h.Month=#{month}
and h.dbtype = #{dbtype,jdbcType=VARCHAR}
and h.dbbk = #{dbbk,jdbcType=VARCHAR}
and u.UserCode=h.UserCode and u.UserCode =m.EmployeeCode and u.RoleType='0' and u.Flag='0' and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode
and r.RoleCode in("100","150","200","300","50")
and h.Year=m.Year and m.Month=h.Month AND h.dbtype = m.dbtype order by h.Year+ 0 desc,h.Month+0 desc, h.ScoreStatus

SELECT h.Id, h.UserCode, h.Year, h.Month, h.ScoreStatus, h.AScore, h.BScore, h.CScore, h.DScore, h.TotalScore, h.SumZdScore, h.SumMbScore, h.AvgZdScore, h.AvgMbScore, h.SumTotalScore, h.DfScore, u.postType, u.dbtype, u.dbbk, h.SumZfScore, u.MoneyCard, u.BranchCode FROM scorehistory h, USER u, monthsummary m, role r, user_role ur WHERE u.UserCode = h.UserCode AND u.UserCode = m.EmployeeCode AND u.RoleType = '0' AND u.Flag = '0' AND u.UserCode = ur.UserCode AND r.RoleCode = ur.RoleCode
AND r.RoleCode IN ( "100", "200", "300", "50" )
AND h.YEAR = m.YEAR AND m.Month = h.Month AND h.dbtype = m.dbtype AND h.Year=#{year} AND h.Month=#{month}
and h.dbtype = #{dbtype,jdbcType=VARCHAR}
and h.dbbk = #{dbbk,jdbcType=VARCHAR}

SELECT
    s.*
FROM
    monthsummary m,
    scorehistory s
where
    m.Year = s.Year
    AND m.Month = s.Month
    AND m.dbtype = s.dbtype
    AND m.EmployeeCode = s.UserCode
    AND m.Year = #{year}
    AND m.Month = #{month}
    AND m.dbtype = #{dbtype}

        AND m.EmployeeCode in (
            select UserCode from user where Flag='0' and UserState='1' and RoleType='0' and postType = #{postType}
        )


        AND m.EmployeeCode = #{userCode}

mapper.delete:
delete from scorehistory where year=#{year} and Month=#{month} and dbtype=#{dbtype}

    AND UserCode in (
        select UserCode from user where postType = #{postType}
    )


    AND UserCode = #{userCode}
