mapper.select:
select count(*) from monthsummary m,score s,role r,user_role ur,user u
where  s.ScorringCode=m.EmployeeCode
and m.Year=#{year}
and m.`Month`=#{month}
and m.dbtype =#{dbtype}
and m.EmployeeCode=#{employeecode}
and s.ScorredCode=ur.UserCode
and r.RoleCode=ur.RoleCode

  and r.RoleCode in("100","200","300","50")

and u.UserCode=ur.UserCode
and u.UserState="1"
and u.Flag="0"

select u.UserCode,u.UserName,u.MoneyCard, m.SerialNo,m.Year,m.Month,m.ScoreStatus, s.StationName,s.StationCode,d.DepartmentCode,d.DepartmentName, r.RoleCode,r.RoleName from user u ,monthsummary m,station s,department d,role r,user_role ur
and u.dbbk in('3','4')
and u.UserName like concat('%',#{username},'%')
and u.StationCode=#{stationcode}
and m.Year=#{year}
and m.Month=#{month}
and m.ScoreStatus=#{scorestatus}
and u.UserCode =m.EmployeeCode and u.RoleType="0" and u.Flag="0" and u.StationCode=s.StationCode and s.DepartmentCode=d.DepartmentCode and u.UserCode=ur.UserCode and r.RoleCode=ur.RoleCode and r.RoleCode ="150"
order by m.Year+0 desc, m.Month+0 desc,m.Pubdate desc
