mapper.select:
select u.UserCode,u.UserName,u.Moneycard,
m.SerialNo,m.Title,m.Content,m.Year,m.Month,m.Content,m.State,m.FileName,m.SavePath,m.EmployeeCode,
s.StationCode,s.StationName,d.DepartmentCode,d.DepartmentName,m.dbtype,u.dbbk
from user u,monthsummary m,station s,department d


        and m.Year=#{year}


        and m.Month=#{month}


        and m.dbtype=#{dbtype}


        and m.EmployeeCode=#{employeecode}

    and  u.UserCode=m.EmployeeCode
    and u.RoleType="0"
    and u.Flag="0"
    and u.StationCode=s.StationCode
    and d.DepartmentCode=s.DepartmentCode

select u.UserCode,u.UserName,
m.SerialNo,m.Year,m.Month,m.Title,m.State,m.EmployeeCode,
s.* from user u ,monthsummary m,score s


        and u.UserName like concat('%',#{username},'%')


        and u.StationCode=#{stationcode}


        and m.Year=#{year}


        and m.dbtype=#{dbtype}


        and m.Month=#{month}

    and u.UserCode =m.EmployeeCode
    and m.EmployeeCode=s.ScorredCode
    and s.ScorringCode=#{scorringcode}
    and m.dbtype = s.dbtype
    and m.State ="6"
    and u.RoleType="0"
    and u.Flag="0"

    and u.UserState="1"

select distinct u.UserCode,u.UserName,
m.SerialNo,m.Year,m.Month,m.Title,m.State,m.EmployeeCode,
s.ScorringCode,s.ScorredCode,UUID() Id, null ScoreType, null DutyCode from user u ,monthsummary m,score s


        and u.UserName like concat('%',#{username},'%')


        and u.StationCode=#{stationcode}


        and m.Year=#{year}


        and m.Month=#{month}

    and u.UserCode =m.EmployeeCode
    and m.EmployeeCode=s.ScorredCode
    and m.dbtype = s.dbtype
    and s.ScorringCode=#{scorringcode}
    and m.dbtype = #{dbtype}
    and m.State ="6"
    and u.RoleType="0"
    and u.Flag="0"
    and u.UserState="1"

select u.*,m.*,s.* from user u ,monthsummary m,score s


        and m.Year=#{year}


        and m.Month=#{month}


        and m.dbtype=#{dbtype}


        and u.UserName like concat('%',#{username},'%')


        and u.StationCode=#{stationcode}

    and u.UserCode =m.EmployeeCode
    and m.EmployeeCode=s.ScorredCode
    and m.dbtype = s.dbtype
    and s.ScorringCode=#{scorringcode}
    and m.State in ("6","7")
    and u.RoleType="0"
    and u.Flag="0"
    and u.UserState="1"

select u.*,m.*,u.UserCode ScorredCode from user u ,monthsummary m


        and m.Year=#{year}


        and m.Month=#{month}

    and m.dbtype=#{dbtype}
    and u.UserCode =m.EmployeeCode
    AND m.EmployeeCode in (
        select ScorredCode from score where dbtype = #{dbtype} and ScorredCode=#{scorredcode}
    )
    and m.State in ('5','6','7')
    and u.RoleType="0"
    and u.Flag="0"
    and u.UserState="1"

select distinct u.UserCode,u.UserName, m.SerialNo,m.Year,m.Month,m.Title,m.State,m.EmployeeCode,s1.StationCode,s1.StationName,d.DepartmentCode,d.DepartmentName, s.ScorringCode,s.ScorredCode,UUID() Id, null ScoreType, null DutyCode from user u ,monthsummary m,score s,station s1,department d
and m.Year=#{year}
and m.Month=#{month}
and u.UserName like concat('%',#{username},'%')
and u.StationCode=#{stationcode}
and u.UserCode =m.EmployeeCode and m.EmployeeCode=s.ScorredCode and m.dbtype = #{dbtype} and m.dbtype = s.dbtype and s.ScorringCode=#{scorringcode} and u.StationCode=s1.StationCode and d.DepartmentCode=s1.DepartmentCode and m.State in ("6","7") and u.RoleType="0" and u.Flag="0" and u.UserState="1"
